import { useState } from "react";

function MainDivFunctional(){
    const [counter, setCounter] = useState(0);
    const [toDoList, setToDoList] = useState([]);
    const [toDoItemAdd, setToDoItemAdd] = useState("");
    const [toDoItemRemove, setToDoItemRemove] = useState("");
    const [warning, setWarning] = useState("");

    const handleAdd = () => {
        if(toDoItemAdd != ""){
            setCounter(counter + 1);
            let tempArr = toDoList;
            tempArr.push(toDoItemAdd);
            setToDoList(tempArr);
            setToDoItemAdd("");
        }else{
            setWarning("Please enter an item.");
        }
    };

    const handleRemoveAll = () => {
        setCounter(0);
        setToDoList([]);
    };

    const handleRemove = (event) => {
        setCounter(counter - 1);
        let tempArr = toDoList;
        tempArr.remove(toDoItemRemove);
        setToDoList(tempArr);
    }

    const toDoListHTML = () => {
        return(
            <div>
            {toDoList.map((item, index) => (
                <div key={index} className="toDoListDiv">
                    <h1>{item}</h1>
                    <button onClick={handleRemove}>REMOVE</button>
                </div>))}
            </div>
        );
    };

    const handleOnChange = (event) => {
        if(warning != ""){
            setWarning("");
        }
        setToDoItemAdd(event.target.value);
        console.log(toDoItemAdd);
    };

    return(
        (<div className="container">
            <div className="div1">
                <input className="input" type = "text" value={toDoItemAdd} onChange={handleOnChange}></input>
                <button className="button1" onClick={handleAdd}>ADD</button>
                <button className="button2" onClick={handleRemoveAll}>REMOVE ALL</button>
            </div>
            <div className="div2">
                <h1>Item Count: {counter}</h1>
                <h1>{warning}</h1>
            </div>
            <div className="div3">
                {toDoListHTML}
            </div>
        </div>)
    );
}

export default MainDivFunctional;
