import { useState } from "react";

function MainDivFunctional(){
    const [counter, setCounter] = useState(0);
    const [toDoList, setToDoList] = useState(["Deneme"]);
    const [toDoItem, setToDoItem] = useState("");

    const handleAdd = () => {
        setCounter(counter + 1);
        let tempArr = [].concatArray(toDoList);
        tempArr.push(toDoItem);
        setToDoList(tempArr);
    };

    const handleRemoveAll = () => {
        setCounter(0);
    };

    const toDoListHTML = toDoList.map((item, index) => (
        <li key={index}>
            {item}
        </li>
    ));

    const handleOnChange = (event) => {
        setToDoItem(event.target.value);
    };

    return(
        (<div className="container">
            <div className="div1">
                <input className="input" type = "text" value={toDoItem} onChange={handleOnChange}></input>
                <button className="button1" onClick={handleAdd}>ADD</button>
                <button className="button2" onClick={handleRemoveAll}>REMOVE ALL</button>
            </div>
            <div className="div2">
                <h1>Item Count: {counter}</h1>
            </div>
            <div className="div3">
                <ul>
                    {toDoListHTML}
                </ul>
            </div>
        </div>)
    );
}

export default MainDivFunctional;
