import {Component} from "react";

class MainDiv extends Component{
    constructor() {
        super();
        this.state = {
            toDoList: [],
            counter: 0
        }
    }

    increment(){
        this.setState({
            counter: 0
        });
    }

    render() {
        return(
            (<div>
                <div>
                    <input type = "text"></input>
                    <button onClick={()=> this.increment()}>Add</button>
                </div>
                <div>
                    <h3>Item Count: {this.state.counter}</h3>
                </div>
                <div>
                    <ul>

                    </ul>
                </div>
            </div>)
        )
    }
}

export default MainDiv;
